import React, { useState, useEffect } from 'react';
import { useDataProvider, Loading, Error, Button} from 'react-admin';
import {
    ResponsiveContainer, AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip,
} from 'recharts';

// const data = [
//     {name: 'Page A', uv: 4000, pv: 2400, amt: 2400},
//     {name: 'Page B', uv: 3000, pv: 1398, amt: 2210},
//     {name: 'Page C', uv: 2000, pv: 9800, amt: 2290},
//     {name: 'Page D', uv: 2780, pv: 3908, amt: 2000},
//     {name: 'Page E', uv: 1890, pv: 4800, amt: 2181},
//     {name: 'Page F', uv: 2390, pv: 3800, amt: 2500},
//     {name: 'Page G', uv: 3490, pv: 4300, amt: 2100},
// ];

function getDaysInMonth(month, year) {
    var date = new Date(year, month, 1);
    var days = [];
    while (date.getMonth() === month) {
      days.push({ date: date.getDate(), score: 0 });
      date.setDate(date.getDate() + 1);
    }
    return days;
}

const getDateTime = value => {
    if (value === undefined) {
        return "";
    } else {
        const timestamp = new Date(value === undefined ? new Date() : value * 1000);
        // return timestamp.toLocaleString();
        return timestamp.toLocaleDateString();
    }

};

const DashChart = (props) => {
    // static jsfiddleUrl = 'https://jsfiddle.net/alidingling/Lrffmzfc/';
    // const width = 

    const dataProvider = useDataProvider();
    const [data, setData] = useState();
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState();

    const [total, setTotal] = useState();


    useEffect(() => {

        dataProvider('GET_LIST', 'notifications', {
            // filter: { report_timestamp: { "$gte": {}}},

            sort: { field: 'report_timestamp', order: 'ASC' },
            pagination: { page: 1, perPage: 100000 },
        })
        // dataProvider.getList('notifications', { sort: {report_timestamp:'ASC'}, filter: {} })
            .then(({ data, total }) => {
                setData(data); 
                setTotal(total);                
                setLoading(false);
            })
            .catch(error => {
                setError(error);
                setLoading(false);
            })
    }, []);

    var chartData = [];

    if (loading) return <Loading />;
    if (error) return <Error />;

    // setCount(Number(0));

    var preDate = ""
    data.map(async answer => {
        if (!data || data.length === 0) {
            return;
        }

        const selDate = getDateTime(answer.report_timestamp);

        if (preDate === "" ) {
            chartData[chartData.length] = {
                name: selDate,
                Notifications  : 1
            };
            preDate = selDate;
        } else if (preDate === selDate) {
            chartData[chartData.length - 1].Notifications += 1;
        } else {
            chartData[chartData.length] = {
                name: selDate,
                Notifications  : 1
            };
            preDate = selDate;
        }
    });

    return (
        <ResponsiveContainer height={250} width='99%'>
            <AreaChart width={500} height={400} data={chartData} margin={{ top: 10, right: 30, left: 0, bottom: 0,}}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Area type="monotone" dataKey="Notifications" stroke="#8884d8" fill="#8884d8" />
            </AreaChart>
        </ResponsiveContainer>  

    );


            {/* downloadImage.bind(this, element)  */}
}

export default DashChart;
