import React, { useState, useEffect } from 'react';
import { useDataProvider, Loading, Error} from 'react-admin';
import {
    ResponsiveContainer, AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip,
} from 'recharts';
import PropTypes from 'prop-types';

// const data = [
//     {name: 'Page A', uv: 4000, pv: 2400, amt: 2400},
//     {name: 'Page B', uv: 3000, pv: 1398, amt: 2210},
//     {name: 'Page C', uv: 2000, pv: 9800, amt: 2290},
//     {name: 'Page D', uv: 2780, pv: 3908, amt: 2000},
//     {name: 'Page E', uv: 1890, pv: 4800, amt: 2181},
//     {name: 'Page F', uv: 2390, pv: 3800, amt: 2500},
//     {name: 'Page G', uv: 3490, pv: 4300, amt: 2100},
// ];

function getTimestampFirstDayOfKey(datetype) {
    var date = new Date();
    date.setDate(1);
    if (datetype === "month") {
        return Math.round(Number(date.valueOf() / 1000));
    } else {
        date.setDate(0);
        return Math.round(Number(date.valueOf() / 1000));
    }

}

const getDateTime = (value, datetype) => {
    if (value === undefined) {
        return "";
    } else {
        const timestamp = new Date(value === undefined ? new Date() : value * 1000);
        var year = timestamp.getFullYear();
        var month = (1 + timestamp.getMonth()).toString();
        month = month.length > 1 ? month : '0' + month;

        // return timestamp.toLocaleString();
        if (datetype === "month")
            return timestamp.toLocaleDateString();
        else 
            return month + "/" + year;
    }
};

const DashChart = ({ datetype }) => {
    // static jsfiddleUrl = 'https://jsfiddle.net/alidingling/Lrffmzfc/';
    // const width = 

    const dataProvider = useDataProvider();
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState();
    const [total, setTotal] = useState();


    useEffect(() => {

        dataProvider('GET_LIST', 'notifications', {
            sort: { field: 'report_timestamp', order: 'ASC' },
            pagination: { page: 1, perPage: 10000000 },
        })
            .then(({ data, total }) => {
                setData(data); 
                setTotal(total);                
                setLoading(false);
            })
            .catch(error => {
                setError(error);
                setLoading(false);
            })
    }, []);

    var chartData = [];

    console.log(data.length);
    if (loading) return <Loading />;
    if (error) return <Error />;

    var preDate = ""
    data.map(async answer => {
        if (!data || data.length === 0) {
            return;
        }

        if (answer.report_timestamp < getTimestampFirstDayOfKey(datetype)) {
            return;
        }

        const selDate = getDateTime(answer.report_timestamp, datetype);

        console.log("datetype: " + datetype + ", selDate:" + selDate);

        if (preDate === "" ) {
            chartData[chartData.length] = {
                name: selDate,
                Notifications  : 1
            };
            preDate = selDate;
        } else if (preDate === selDate) {
            chartData[chartData.length - 1].Notifications += 1;
        } else {
            chartData[chartData.length] = {
                name: selDate,
                Notifications  : 1
            };
            preDate = selDate;
        }
    });

    return (
        <ResponsiveContainer height={250} width='99%'>
            <AreaChart width={500} height={400} data={chartData} margin={{ top: 10, right: 30, left: 0, bottom: 0,}}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Area type="monotone" dataKey="Notifications" stroke="#8884d8" fill="#8884d8" />
            </AreaChart>
        </ResponsiveContainer>  

    );
}

DashChart.propTypes = {
    datetype: PropTypes.string.isRequired,
};

export default DashChart;
