import * as React from "react";
import { Card, CardContent, CardHeader } from '@material-ui/core';
import {DashChart} from '../components'

function getDateByKey(value) {
    var date = new Date();
    var year = date.getFullYear();
    var month = (1 + date.getMonth()).toString();
    month = month.length > 1 ? month : '0' + month;

    if (value === "month")
        return month + "/" + year;
    else 
        return year;
}

export default () => (
    <Card>
        <CardHeader title="Welcome to the administration" />
        <CardContent>Number of notifications per day this month ({getDateByKey("month")}): </CardContent>
        <DashChart datetype="month"/>
        <br />
        <CardContent>Number of notifications per day this year ({getDateByKey("year")}):</CardContent>
        <DashChart datetype="year"/>
    </Card>
);
