import React from 'react';
import {
  List, Datagrid, TextField, 
  Show, SimpleShowLayout,
  DeleteButton, SimpleList,
  DateInput
} from 'react-admin';
import { useMediaQuery } from '@material-ui/core';
import {DateTimeField} from '../components'
// import { PostFilter } from '../components';


export const NotificationList = (props) => {

    const isSmall = useMediaQuery(theme => theme.breakpoints.down('sm'));
//   <List {...props} >
//     <Datagrid rowClick="show">
//       <TextField label="Location" source="address" />
//       <CommentField label="Comments" source="id" />
//       <AudioPlayerField label="Voice" source="voice_url" />
//       <ConditionBooleanField source="report_users" />
//     </Datagrid>
//   </List>
    return (
        /* filters={<PostFilter />} */
        <List {...props}>
            {isSmall ? (
                <SimpleList
                    primaryText={record => record.report_title}
                    secondaryText={record => `${record.report_content} views`}
                    tertiaryText={record => new Date(record.report_timestamp * 1000).toLocaleDateString()}
                    linkType={record => record.canEdit ? "edit" : "show"}
                />
            ) : (
                <Datagrid>

                    <TextField label="Title" source="report_title" />
                    <TextField label="Content" source="report_content" />
                    <DateTimeField label='Date' source='report_timestamp' />
                    {/* <DeleteButton basePath={id} record={data} resource={resource}/> */}

                    {/* <DateInput label="Publication date" source="published_at" defaultValue={new Date()} /> */}

                </Datagrid>
            )}
        </List>
    )
};

export const NotificationShow = props => (
  <Show {...props}>
    <SimpleShowLayout>
        <TextField label="Title" source="report_title" />
        <TextField label="Content" source="report_content" />
        <TextField label="Title" source="report_timestamp" />
        <DateInput label="Publication date" source="published_at" defaultValue={new Date()} />
        <DeleteButton />
    </SimpleShowLayout>
  </Show>
);
