import React, { useState, useEffect } from 'react';
import { useDataProvider, Loading, Error } from 'react-admin';
import {
    ResponsiveContainer, AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip,
} from 'recharts';

// const data = [
//     {name: 'Page A', uv: 4000, pv: 2400, amt: 2400},
//     {name: 'Page B', uv: 3000, pv: 1398, amt: 2210},
//     {name: 'Page C', uv: 2000, pv: 9800, amt: 2290},
//     {name: 'Page D', uv: 2780, pv: 3908, amt: 2000},
//     {name: 'Page E', uv: 1890, pv: 4800, amt: 2181},
//     {name: 'Page F', uv: 2390, pv: 3800, amt: 2500},
//     {name: 'Page G', uv: 3490, pv: 4300, amt: 2100},
// ];

const getDateTime = value => {
    if (value === undefined) {
        return "";
    } else {
        const timestamp = new Date(value === undefined ? new Date() : value * 1000);
        // return timestamp.toLocaleString();
        return timestamp.toLocaleDateString();
    }

};

const DashChart = () => {
    // static jsfiddleUrl = 'https://jsfiddle.net/alidingling/Lrffmzfc/';
    // const width = 

    const dataProvider = useDataProvider();
    const [data, setData] = useState();
    const [total, setTotal] = useState();
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState();

    useEffect(() => {

        dataProvider('GET_LIST', 'notifications', {
            // filter: { date_gte: aMonthAgo.toISOString() },
            sort: { field: 'report_timestamp', order: 'DESC' },
            pagination: { page: 1, perPage: 100000 },
        })
        // dataProvider.getList('notifications', { sort: {report_timestamp:'ASC'}, filter: {} })
            .then(({ data, total }) => {
                setData(data); 
                setTotal(total);                
                setLoading(false);
            })
            .catch(error => {
                setError(error);
                setLoading(false);
            })
    }, []);

    var chartData = [];
    if ( data) {
        data.map(async answer => {
            if (!data || data.length === 0) {
                return;
            }

            chartData[chartData.length] = {
                name: getDateTime(answer.report_timestamp),
                uv  : answer.report_timestamp - 1606950000
            };

            // const scores = answer.score.split(",");
            // const day = new Date(answer.date).getDate() + 1;
            // if(scores.length > 1) {
            // var total = 0;
            // scores.forEach(item => {
            //     total += Number(item);
            // });
            // answer.score = (total / scores.length).toFixed(0) + "";
            // }
            // if(answers[answer.question_id] === undefined) {
            // answers[answer.question_id] = { question: answer.question, answer: getDaysInMonth(month, year)};
            // if (answers[answer.question_id].answer[day] !== undefined) {
            //     answers[answer.question_id].answer[day].score = answer.score;
            // }
            // } else {
            // if (answers[answer.question_id].answer[day] !== undefined) {
            //     answers[answer.question_id].answer[day].score = answer.score;
            // }
            // }
        });


    }
    if (loading) return <Loading />;
    if (error) return <Error />;

    return (
        <AreaChart width={500} height={400} data={chartData} margin={{ top: 10, right: 30, left: 0, bottom: 0,}}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Area type="monotone" dataKey="uv" stroke="#8884d8" fill="#8884d8" />
        </AreaChart>
    );
}

export default DashChart;
